import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from "react-redux";
import classes from './Company.module.css';
import news_icon from './news_pic.jpeg';


import {
    selectSheet,
    getSpredSheet,
} from "../company/companySlice";

const Company = (info) => {
    const dispatch = useDispatch();
    const sheet = useSelector(selectSheet);
    const other_id = (number) => {
        let dir = window.location.href.split("/");
        let dir2 = dir[dir.length - number];
        return dir2;
    }
    const table = sheet[other_id(2) + '_' + 'table'] ? sheet[other_id(2) + '_' + 'table'] : sheet['' + '_' + 'table']
    const news = sheet[other_id(2) + '_' + 'news'] ? sheet[other_id(2) + '_' + 'news'] : sheet['' + '_' + 'news']
    const keywords = sheet[other_id(2) + '_' + 'keyword'] ? sheet[other_id(2) + '_' + 'keyword'] : sheet['' + '_' + 'keywords']

    var roma_company = {}
    for (var a in sheet.info.elements) {
        roma_company[sheet.info.elements[a].ローマ字] = sheet.info.elements[a].企業名
    }

    useEffect(async() => {
        const res = await dispatch(getSpredSheet())
    }, []);



    return (
        <div>
            <header className={classes.header}>
                <div>{roma_company[table.name.slice(0, -6)]}</div>
            </header>
            {keywords &&
                <div>
                    <h2 className={classes.head}>Keywords</h2>
                    <div className={classes.keyword_explain}>複数の記事を自然言語処理にかけkeywordを抽出。</div>
                    <div className={classes.keyword_box}>
                    <br/>
                        {keywords.elements.map((each) => (
                            <div className={classes.keyword_position}>
                                <div className={classes.keyword}>{each.keyword}</div>
                            </div>
                        ))}
                    </div>
                </div>
            }
            {table && 
                <div>
                    <h2 className={classes.head}>募集要項</h2>
                    <div className={classes.table_box}>
                        {table.elements.map((each) => (
                            <dl>
                                <dt className={classes.tag_dt}>{each.項目}</dt>
                                <dt>{each.内容}</dt>
                            </dl>
                        ))}
                    </div>
                </div>
            }
            {news &&
                <div>
                    <h2 className={classes.head}>注目記事まとめ</h2>
                    <div className={classes.news_box}>
                        {news.elements.map((each) => (
                            <div className={classes.news_all}>
                                <div className={classes.each_news}>
                                    <img src={news_icon} className={classes.news_icon}/>
                                    <div className={classes.news_card}>
                                    </div>
                                    <div  className={classes.news_position}>
                                        <a href={each.リンク} className={classes.news}>{each.題名}</a>
                                    </div>
                                </div>
                            </div>
                        ))}    
                    </div>
                </div>
            }
            <footer className={classes.header}>
                <div>{roma_company[table.name.slice(0, -6)]}</div>
            </footer>
        </div>
    )
}

export default Company
