import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from "react-redux";
import classes from './Property.module.css';

import {
    selectSheet,
    getSpredSheet,
} from "../company/companySlice";

const Property = () => {
    const dispatch = useDispatch();
    const sheet = useSelector(selectSheet);
    var roma_company = {}

    // 名前の変更用
    for (var a in sheet.info.elements) {
        roma_company[sheet.info.elements[a].ローマ字] = sheet.info.elements[a].企業名
    }

    // tableだけ取り出す
    var table_list = [];
    for(var key of Object.keys(sheet)){
        if(key.match(/_table/)){
            table_list.push(sheet[key])
        } 
    }

    useEffect(async() => {
        const res = await dispatch(getSpredSheet())
    }, []);

    return (
        <div>
            <header className={classes.header}>
                <div>給与</div>
            </header>
            <div>
                <h2 className={classes.head}>給与一覧</h2>
                <div className={classes.property_box}>
                    {table_list.map((table) => (
                        table.elements.map((tab) => {
                            if (tab.項目 == '初任給'){
                                return (
                                <dl className={classes.tag_dl}>
                                    <dt className={classes.tag_dt}>{roma_company[table.name.slice(0, -6)]}</dt>
                                    <dd className={classes.tag_dd}>{tab.内容}</dd>
                                </dl>
                                )
                            }
                        })
                    ))}
                </div>
            </div>

            <footer className={classes.header}>
                <div>給与</div>
            </footer>
        </div>
    )
}

export default Property
