import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from "react-redux";
import classes from './Property.module.css';

import {
    selectSheet,
    getSpredSheet,
} from "../company/companySlice";

const Property = () => {
    const dispatch = useDispatch();
    const sheet = useSelector(selectSheet);

    // urlの取り出し
    const other_id = (number) => {
        let dir = window.location.href.split("/");
        let dir2 = dir[dir.length - number];
        return dir2;
    }

    // 名前の変更用辞書
    var roma_company = {}
    for (var a in sheet.info.elements) {
        roma_company[sheet.info.elements[a].ローマ字] = sheet.info.elements[a].企業名
    }

    // tableだけ取り出す
    var table_list = [];
    for(var key of Object.keys(sheet)){
        if(key.match(/_table/)){
            table_list.push(sheet[key])
        } 
    }

    const target_dic = {'給与': ['初任給'], '休暇': ['休日', '休暇'], '勤務地': ['勤務地'] }
    const target = target_dic[decodeURI(other_id(2))]

    useEffect(async() => {
        const res = await dispatch(getSpredSheet())
    }, []);

    return (
        <div>
            <header className={classes.header}>
                <div>{decodeURI(other_id(2))}</div>
            </header>
            <div>
                <h2 className={classes.head}>{decodeURI(other_id(2))}一覧</h2>
                <div className={classes.property_box}>
                    {table_list.map((table) => (
                        table.elements.map((tab) => {
                            for (let i = 0; i < target.length; i++){
                                if (tab.項目.match(target[i])){
                                    return (
                                    <dl className={classes.tag_dl}>
                                        <dt className={classes.tag_dt}>{roma_company[table.name.slice(0, -6)]}</dt>
                                        <dd className={classes.tag_dd}>{tab.内容}</dd>
                                    </dl>
                                    )
                                break;
                                }
                            }
                        })
                    ))}
                </div>
            </div>

            <footer className={classes.header}>
                <div>{decodeURI(other_id(2))}</div>
            </footer>
        </div>
    )
}

export default Property
