import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import Tabletop from 'tabletop';
import axios from "axios";

const apiUrl = "http://3.14.82.87:1337/";

export const getSpredSheet = createAsyncThunk("company/get", async() => {
    const res = Tabletop.init({
        key: '1iQhGbFVfEGXLVzrsbxKi-SwVsXkDWI_XyrrFok8rTdk',
    })
    .then((data) => {
        return data
    })
    return res
})

export const fetchAsynCampanyList_Get = createAsyncThunk("company_list/get", async () => {
    const res = await axios.get(`${apiUrl}api/company/`, {
      headers: { 
      },
    });
    return res.data;
});

const companySlice = createSlice({
    name: 'company',
    initialState: {
        info: {
            elements: [{id: 0, ローマ字: '', 企業名: '', 分類: ''},],
        },
        sheet: {
            info: {
                elements: [{id: 0, ローマ字: '', 企業名: '', 分類: ''},],
            },
            _table: {
                elements: [{項目: '', 内容: '', url: ''},],
                name: '',
            },
            _news: {
                elements: [{題名: '', リンク: '', url: ''},],
                name: '',
            },
            _keywords: {
                elements: [{題名: '', リンク: '', url: ''},],
                name: '',
            },
        },
        company_list: {id: '', name: '', keyword: {}, news: {}, recruit: {}},

    },
    reducers: {

    },
    extraReducers: (builder) => {
        builder.addCase(getSpredSheet.fulfilled, (state, action) => {
            state.sheet = action.payload;
        })
        builder.addCase(fetchAsynCampanyList_Get.fulfilled, (state, action) => {
            state.company_list = action.payload;
        })
    }
})

export const { } = companySlice.actions;
export const selectSheet = (state) => state.company.sheet;

export default companySlice.reducer;
